import { HereNowParameters, HereNowResponse } from "pubnub";
import { usePubNub } from "pubnub-react";
import cloneDeep from "lodash.clonedeep";

type ChannelsOccupancy = HereNowResponse["channels"];
type HookReturnValue = [ChannelsOccupancy, () => Promise<void>, number, Error];

export const usePresence = (options: HereNowParameters = {}): HookReturnValue => {
  const pubnub = usePubNub();

  const [presence, setPresence] = useState<ChannelsOccupancy>({});
  const [error, setError] = useState<Error>();

  const presenceValues = Object.values(presence);
  const total = presenceValues.map((ch) => ch.occupancy).reduce((prev, cur) => prev + cur, 0);

  const command = useCallback(async () => {
    try {
      const response = await pubnub.hereNow(options);
      setPresence(response.channels);
    } catch (e) {
      setError(e);
    }
  }, [pubnub, JSON.stringify(options)]);

  const handlePresence = useCallback(
    (event) => {
      setPresence((presence) => {
        const presenceClone = cloneDeep(presence);
        if (!presenceClone[event.channel])
          presenceClone[event.channel] = { name: event.channel, occupancy: 0, occupants: [] };
        const channel = presenceClone[event.channel];

        if (event.action === "join") {
          if (!channel.hasOwnProperty("occupants")) channel.occupants = [];
          channel.occupancy = event.occupancy;

          if (
            options.includeUUIDs !== false &&
            channel.hasOwnProperty("occupants") &&
            !channel.occupants.find((u) => u.uuid == event.uuid)
          ) {
            const { state, uuid } = event;
            channel.occupants.push({ state, uuid });
          }
        }

        if (["leave", "timeout"].includes(event.action)) {
          channel.occupancy = event.occupancy;

          if (
            options.includeUUIDs !== false &&
            channel.hasOwnProperty("occupants") &&
            channel.occupants.find((u) => u.uuid == event.uuid)
          ) {
            presenceClone[event.channel].occupants = channel.occupants.filter(
              (u) => u.uuid !== event.uuid
            );
          }
        }

        return presenceClone;
      });
    },
    [options.includeUUIDs]
  );

  useEffect(() => {
    pubnub.addListener({ presence: handlePresence });
  }, [handlePresence, pubnub]);

  useEffect(() => {
    setPresence({});
    setError(null);
    command();
  }, [command]);

  return [presence, command, total, error];
};
import { useState, useEffect } from "react";
import { UUIDMetadataObject, ObjectCustom, GetUUIDMetadataParameters } from "pubnub";
import { usePubNub } from "pubnub-react";
import cloneDeep from "lodash.clonedeep";

export const useUser = (
  options: GetUUIDMetadataParameters = {}
): [UUIDMetadataObject<ObjectCustom>, Error] => {
  const pubnub = usePubNub();

  const [user, setUser] = useState(null);
  const [error, setError] = useState<Error>();

  const command = async () => {
    try {
      const response = await pubnub.objects.getUUIDMetadata(options);
      setUser(response.data);
    } catch (e) {
      setError(e);
    }
  };

  const handleObject = (event) => {
    const message = event.message;
    if (message.type !== "uuid") return;

    setUser((user) => {
      const userCopy = cloneDeep(user);

      if (message.data.id == user.id) {
        Object.assign(userCopy, message.data);
      }

      return userCopy;
    });
  };

  useEffect(() => {
    pubnub.addListener({ objects: handleObject });
    command();
  }, []);

  return [user, error];
};
import { useEffect } from "react";
import { SubscribeParameters } from "pubnub";
import { usePubNub } from "pubnub-react";

export const useSubscribe = (options: SubscribeParameters = {}): (() => void) => {
  const pubnub = usePubNub();

  const unsubscribe = () => {
    pubnub.unsubscribe(options);
  };

  useEffect(() => {
    options.channels = options.channels || [];
    options.channelGroups = options.channelGroups || [];

    const currentSubscriptions = pubnub.getSubscribedChannels() || [];
    const currentGroups = pubnub.getSubscribedChannelGroups() || [];

    const subscribeChannels = options.channels.filter((c) => !currentSubscriptions.includes(c));
    const unsubscribeChannels = currentSubscriptions.filter((c) => !options.channels.includes(c));

    const subscribeGroups = options.channelGroups.filter((c) => !currentGroups.includes(c));
    const unsubscribeGroups = currentGroups.filter((c) => !options.channelGroups.includes(c));

    if (subscribeChannels.length || subscribeGroups.length) {
      pubnub.subscribe({
        channels: subscribeChannels,
        channelGroups: subscribeGroups,
        withPresence: options.withPresence,
      });
    }

    if (unsubscribeChannels.length || unsubscribeGroups.length) {
      pubnub.unsubscribe({
        channels: unsubscribeChannels,
        channelGroups: unsubscribeGroups,
      });
    }
  }, [JSON.stringify(options.channels), JSON.stringify(options.channelGroups)]);

  return unsubscribe;
};
